import React, { Component } from "react";
import CardList from "./CardList";
import SearchBox from "./SearchBox";
// import { bio } from './robot';
import './App.css';


class App extends Component {
    constructor() {
        super();
        this.state = {
            info: [],
            searchField: ''
        }
    }
    componentDidMount() {
        fetch('https://jsonplaceholder.typicode.com/users')
            .then(response => response.json())
            .then(users => {this.setState({info:users})})
    }

    onSearch = (event) => {
        this.setState({searchField : event.target.value})
    }

    render() {
        const filteredBio = this.state.info.filter(bio => {
            return bio.name.toLowerCase().includes(this.state.searchField.toLowerCase())
        })
        if (info.length === 0)
        return (
            <div className='tc'>
                <h1 className="f2">RoboFriend </h1>
                <SearchBox searchChange={this.onSearch}/>
                <CardList  bio={filteredBio}/>
            </div>
        );
    }
}

 export default App;
